mapper.select:
SELECT *
FROM MEMBER 
WHERE TYPE = 'CLIENT'
AND NAME = #{userName}
AND PHONE = #{phone}
AND EMAIL = #{email}

SELECT *
FROM MEMBER 
WHERE TYPE = 'CLIENT'
AND NAME = #{userName}
AND EMAIL = #{email}

SELECT *
FROM RFD
WHERE HNAME = 'HotelBoutique'
AND LTYPE = 'RFD'

	AND TERM_TYPE = #{termType}
	AND DAY_TYPE = #{dayType}
	AND RFD_DATE = #{rfdDate}

ORDER BY TERM_TYPE DESC, DAY_TYPE, TO_NUMBER(RFD_DATE) DESC

mapper.update:
UPDATE RSV
SET RSV_STATUS = 'REFUND', MOD_DATE = TO_CHAR(SYSDATE + 9 / (24) + 135 / (24 * 60 * 60), 'YYYY-MM-DD HH24:mi:ss')
WHERE RSV_NO = #{rsvNo}

UPDATE SVC_USE
SET USE_STATUS = 'N'
WHERE RSV_NO = #{rsvNo}

mapper.select:
SELECT SVC_NO
FROM SVC_USE
WHERE RSV_NO = #{rsvNo}

SELECT QPWD
FROM QUE
WHERE QNO = #{qno}
