mapper.select:
SELECT COUNT(*)
FROM REPORT
WHERE RPT_STATUS = #{cateCh}

		SELECT INAME, INO
		FROM (SELECT INAME, MAX(INO) AS "INO"
		FROM CONN C
		JOIN ITEM I ON(C.CN_CODE = I.CN_CODE)
        JOIN ITEM_TYPE IT ON(I.TYPE_NO = IT.TYPE_NO)
		WHERE MFG = #{mfg}
		AND CN_NAME = #{cname}
        AND TYPE = #{type}
		GROUP BY INAME)

SELECT MNO
FROM STAFF 
WHERE STF_ID = #{stfId}

SELECT INAME
FROM ITEM
WHERE INO = #{ino}

SELECT RPT_NO
FROM REPORT
WHERE DOC_NO = #{docNo}

SELECT CN_CODE
FROM CONN
WHERE CN_NAME = #{cnName}

SELECT COUNT(*)
FROM REPORT
WHERE RECEIVER = #{mno}
AND RPT_STATUS = #{category}

	UPDATE REPORT
	SET RPT_STATUS = #{rptStatus},
    	APPR_DATE = #{apprDate}
    WHERE RPT_NO = #{rptNo}

SELECT RPT_NO
FROM REPORT
WHERE DOC_NO = #{docno}

SELECT MNO
FROM STAFF
WHERE STF_ID = #{stfId}

SELECT CN_CODE
FROM CONN
WHERE CN_NAME = #{cname}

SELECT COUNT(*)
FROM REPORT
WHERE MNO = #{myMno}

SELECT *
FROM REPORT
WHERE RPT_NO = #{rptNo}

SELECT DISTINCT TO_CHAR(RCV_REQ_DATE) AS "REQDATE"
FROM ORDER_REQ
WHERE RPT_NO = #{rptNo}
