mapper.select:
SELECT COUNT(*)
FROM ROOM
WHERE RT_NO = #{rtNo}

UPDATE STAFF
SET STF_PWD = #{userPwd}
WHERE MNO = #{mno}

UPDATE STAFF
SET PWD_STATUS = 'N'
WHERE MNO = #{mno}

UPDATE MEMBER
SET NAME = #{userName}, PHONE = #{phone}, EMAIL = #{email}, STATUS = #{status}
WHERE MNO = #{mno}

UPDATE STAFF
SET DEPT_NO = #{deptNo}, AUTH_NO = #{authNo}
WHERE MNO = #{mno}

SELECT *
FROM SVC
WHERE SVC_CODE = #{svcCode}

UPDATE SVC
SET SVC_PRICE = #{svcPrice}, SVC_STATUS = #{svcStatus}
WHERE SVC_CODE = #{svcCode}

UPDATE BROKEN_HIS
SET BRK_STATUS = 'N'
WHERE RM_NO = #{rmNo} AND BRK_BEGIN = #{brkBegin}

SELECT RT_NO
FROM ROOM_TYPE
WHERE RT_NAME = #{rtName}

SELECT RT.RT_NO, RT.MAXPER, RT.MINPER
FROM ROOM_TYPE RT
WHERE RT.RT_NO = #{rtNo}

UPDATE ROOM
SET RM_STATUS = 'N'
WHERE RM_NO = #{rmNo}

UPDATE ROOM
SET RM_STATUS = 'Y'
WHERE RM_NO = #{rmNo}
