mapper.update:
UPDATE ITEM
SET ITEM_STATUS = 'Y'
WHERE INO = #{check}

choose.when:
UPDATE ITEM SET 
AMOUNT=#{amount},
AREA_NO=#{areaNo},
SITE_CODE=#{strgName},
RM_NO=#{rmNo}
WHERE INO = #{ino}

mapper.select:
UPDATE ORDER_HIS
SET RCV_STATUS = 'Y', RCV_DATE = TO_DATE(SYSDATE, 'RRRR-MM-DD HH24:MI:SS')
WHERE RPT_NO = #{reportNo}

SELECT NAME
FROM MEMBER
WHERE MNO = #{receiver}
